</span><span><span class="hljs-comment">// Database configuration</span></span><span>
</span><span><span class="hljs-variable">$host</span></span><span> = </span><span><span class="hljs-string">'localhost'</span></span><span>;
</span><span><span class="hljs-variable">$username</span></span><span> = </span><span><span class="hljs-string">'your_username'</span></span><span>;
</span><span><span class="hljs-variable">$password</span></span><span> = </span><span><span class="hljs-string">'your_password'</span></span><span>;
</span><span><span class="hljs-variable">$database</span></span><span> = </span><span><span class="hljs-string">'your_database'</span></span><span>;

</span><span><span class="hljs-comment">// Backup file name</span></span><span>
</span><span><span class="hljs-variable">$backupFile</span></span><span> = </span><span><span class="hljs-string">'backups/'</span></span><span> . </span><span><span class="hljs-variable">$database</span></span><span> . </span><span><span class="hljs-string">'_'</span></span><span> . </span><span><span class="hljs-title function_ invoke__">date</span></span><span>(</span><span><span class="hljs-string">"Y-m-d_H-i-s"</span></span><span>) . </span><span><span class="hljs-string">'.sql'</span></span><span>;

</span><span><span class="hljs-comment">// Create backups folder if not exists</span></span><span>
</span><span><span class="hljs-keyword">if</span></span><span> (!</span><span><span class="hljs-title function_ invoke__">file_exists</span></span><span>(</span><span><span class="hljs-string">'backups'</span></span><span>)) {
    </span><span><span class="hljs-title function_ invoke__">mkdir</span></span><span>(</span><span><span class="hljs-string">'backups'</span></span><span>, </span><span><span class="hljs-number">0777</span></span><span>, </span><span><span class="hljs-literal">true</span></span><span>);
}

</span><span><span class="hljs-comment">// Use mysqldump to export database</span></span><span>
</span><span><span class="hljs-variable">$command</span></span><span> = </span><span><span class="hljs-string">"mysqldump --user=<span class="hljs-subst">{$username}</span></span></span><span> --password=</span><span><span class="hljs-subst">{$password}</span></span><span> --host=</span><span><span class="hljs-subst">{$host}</span></span><span> </span><span><span class="hljs-subst">{$database}</span></span><span> &gt; </span><span><span class="hljs-subst">{$backupFile}</span></span><span>";
</span><span><span class="hljs-title function_ invoke__">system</span></span><span>(</span><span><span class="hljs-variable">$command</span></span><span>, </span><span><span class="hljs-variable">$output</span></span><span>);

</span><span><span class="hljs-keyword">if</span></span><span> (</span><span><span class="hljs-variable">$output</span></span><span> === </span><span><span class="hljs-number">0</span></span><span>) {
    </span><span><span class="hljs-keyword">echo</span></span><span> </span><span><span class="hljs-string">"✅ Backup completed: <span class="hljs-subst">$backupFile</span></span></span><span>";
} </span><span><span class="hljs-keyword">else</span></span><span> {
    </span><span><span class="hljs-keyword">echo</span></span><span> </span><span><span class="hljs-string">"❌ Backup failed!"</span></span><span>;
}
</span><span><span class="hljs-meta">?&gt;</span></span><span>
</span></span></code></div></div></pre>
